import { useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

function Mainbar({
  getActiveNote,
  notes,
  onAddNote,
  onDeleteNote,
  activeNote,
  setActiveNote,
}) {
  const [quill, setQuill] = useState("");

  return (
    <div id="mainbar">
      <div id="mainbar-header">
        <h1 id="mainbar-title">{activeNote.title}</h1>

        <div id="mainbar-buttons">
          <button id="save_button">save</button>
          {/* i changed this to notes instead of note how do i delete if not?? i need to have note */}

          <button
            onClick={() => onDeleteNote(activeNote.id)}
            id="delete_button"
          >
            delete
          </button>
        </div>
      </div>

      <div id="mainbar-content">
        <ReactQuill theme="snow" value={quill} onChange={setQuill} />
      </div>
    </div>
  );
}
export default Mainbar;
