import { useRef } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

const Main = ({ activeNote, onUpdateNote, onDeleteNote, formatDate }) => {
  const quill = useRef();

  const [value, setValue] = useState("");

  const onEditField = (field, value) => {
    const newNote = {
      ...activeNote,
      [field]: value,
      lastModified: Date.now(),
    };
    onUpdateNote(newNote);
  };

  const onSaveNote = (field, value) => {
    console.log(value);
    // onUpdateNote({
    //   ...activeNote,
    //   [field]: value,
    //   lastModified: Date.now(),
    // });
  };

  if (!activeNote)
    return (
      <div className="no-active-note">Select a note or create a new one </div>
    );

  return (
    <div className="app-main">
      <div className="app-main-note-edit">
        <div id="mainbar-header">
          <h1 id="mainbar-title">
            <input
              type="text"
              id="mainbar-title-input"
              value={activeNote.title}
              onChange={(e) => {
                onEditField("title", e.target.value);
              }}
              autoFocus
            />
          </h1>
          <div id="mainbar-buttons">
            <button
              id="save_button"
              onClick={() =>
                onSaveNote("body", quill.current.getEditor().getText())
              }
            >
              save
            </button>
            <button
              id="delete_button"
              onClick={(e) => onDeleteNote(activeNote.id)}
            >
              delete
            </button>
          </div>
        </div>
        <div id="mainbar-edit">
          <div id="date">
            <input
              type="datetime-local"
              id="date"
              value={new Date(activeNote.lastModified)
                .toISOString()
                .slice(0, -8)}
              onChange={(e) => {
                const newDate = new Date(e.target.value);
                const timestamp = newDate.getTime();
                onEditField("lastModified", timestamp);
              }}
            />
          </div>

          <div id="mainbar-content">
            <ReactQuill
              ref={quill}
              theme="snow"
              value={value}
              onChange={setValue}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Main;
