import { useRef, useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

function edit() {
  const [sortedNotes, setNotes] = useOutletContext();
  const { id } = useParams();
  const note = notesObjects.find((note) => note.ID === id);
  const [body, setBody] = useState(note.noteContent);
  const [when, setWhen] = useState(note.Date);
  const [title, setTitle] = useState(note.Title);

  const navigate = useNavigate(); // add this hook to use navigation

  const onDeleteNote = (noteId) => {
    const answer = window.confirm("Are you sure?");
    if (answer) {
      setNotes(sortedNotes.filter(({ id }) => id !== noteId));
    }
  };

  const onSaveNote = (title, body, when) => {
    const note = notesObjects.find((note) => note.ID === id);
    note.body = body;
    note.Title = title;
    note.notePreview = value;
    note.userDate = when;
    localStorage.setItem("localNotes", JSON.stringify(notesObjects)); // update notes in local storage
    navigate(`/preview/${note.ID}`, { replace: true });
  };

  if (!note) {
    <div className="no-active-note">Select a note or create a new one </div>;
  }
  return (
    <div className="app-main">
      <div className="app-main-note-edit">
        <div id="mainbar-header">
          <h1 id="mainbar-title">
            <input
              type="text"
              id="mainbar-title-input"
              value={activeNote.title}
              onChange={setTitle}
              autoFocus
            />
          </h1>
          <div id="mainbar-buttons">
            <button
              id="save_button"
              onClick={() => onSaveNote(title, body, when)}
            >
              save
            </button>
            <button
              id="delete_button"
              onClick={(e) => onDeleteNote(activeNote.id)}
            >
              delete
            </button>
          </div>
        </div>
        <div id="mainbar-edit">
          <div id="date">
            <input
              type="datetime-local"
              id="date"
              value={when}
              onChange={(e) => {
                setWhen(e.target.value);
              }}
            />
          </div>

          <div id="mainbar-content">
            <ReactQuill
              ref={quill}
              theme="snow"
              value={body}
              onChange={setBody}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default edit;
